mapper.update:
UPDATE member
SET password = #{password}
WHERE id = #{id}

mapper.select:
SELECT COUNT(password)
FROM member
WHERE id = #{id}

SELECT m.nickname, m.email, i.age, i.`type`, i.gender, l.region
FROM member AS m, member_info AS i, location AS l
WHERE m.id = #{id} AND i.region_num = l.num;

SELECT password
FROM member
WHERE id = #{id} AND email = #{email}

SELECT id 
FROM member
WHERE email = #{email}

SELECT COUNT(password)
FROM member
WHERE id = #{id} AND password = #{password}
