mapper.select:
SELECT id, password
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE email = #{email}

mapper.update:
UPDATE member_info

	age = #{age}, type = #{type}, gender = #{gender}, region_num = #{region_num}

WHERE num = #{num}

UPDATE member
SET nickname = #{nickname}
WHERE num = #{num}

UPDATE member
SET password = #{password}
WHERE id = #{id}

mapper.select:
SELECT COUNT(password)
FROM member
WHERE id = #{id}
AND password = #{password}

SELECT num, grade, created_date, id, nickname, email
FROM member
WHERE num = #{num}

SELECT password
FROM member
WHERE id = #{id} AND email = #{email}

SELECT id 
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE id = #{id} AND password = #{password}
